import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import PageDefault from '../../../components/PageDefault';

function CadastroCategoria(){

    const [nomeDaCategoria, setNomeDaCategoria] = useState('Teste');
    const valoresInicias={
      nome: '',
      descricao: '',
      cor:'#000',
    }

    const [values, setValues] = useState(valoresInicias);
    return(
      <PageDefault>
          <h1>Cadastro de categoria: {values.nome} </h1> 

          <form onSubmit={function handleSubmit(infoDoEvento){
            infoDoEvento.preventDefault();
            console.log("voce tentou enviar o form")
            setValues([
              ...values,
              nomeDaCategoria
            ]);
          }}> 

          <div>
            <label>
                Nome da Categoria
                <input type="text" 
                value ={values.nome} //pega o valor do useState
                onChange={function funcaoHandlerQueOErroPediu(infoDoEvento) {//handler tenta caputar o valor do input.. no evento onChange
                console.log('[nomeDaCategoria]', nomeDaCategoria); //
                console.log('[infoDoEvento]', infoDoEvento.target.value);
                //setNomeDaCategoria(infoDoEvento.target.value);// target é o alvo da açao q ta sendo feito.. 
                }
              }/>
            </label>

            <label>
                Descrição:
                <textarea type="text" 
                value ={values.descricao} //pega o valor do useState
                onChange={function funcaoHandlerQueOErroPediu(infoDoEvento) {//handler tenta caputar o valor do input.. no evento onChange
                console.log('[nomeDaCategoria]', nomeDaCategoria); //
                console.log('[infoDoEvento]', infoDoEvento.target.value);
                //setNomeDaCategoria(infoDoEvento.target.value);// target é o alvo da açao q ta sendo feito.. 
                }
              }/>
            </label>

            
          </div>
          <div>
          <label>
                Cor da Categoria
                <input 
                type="color" 
                value ={values.cor} //pega o valor do useState
                onChange={function funcaoHandlerQueOErroPediu(infoDoEvento) {//handler tenta caputar o valor do input.. no evento onChange
                console.log('[nomeDaCategoria]', nomeDaCategoria); //
                console.log('[infoDoEvento]', infoDoEvento.target.value);
                //setNomeDaCategoria(infoDoEvento.target.value);// target é o alvo da açao q ta sendo feito.. 
                }
              }/>
            </label>
          </div>
            <button>
                Cadastrar
            </button>    
          </form> 
          
          <ul>
            {value.categoria.map((categoria, indice) => {
              return(
                <li key={`${categoria}${indice}`}>
                  {categoria}
                </li>
              )
            }
            )}
          
          </ul>
          <Link to="/">
            ir para HOme
          </Link>
      </PageDefault>
    )
  }

  export default CadastroCategoria;
