import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import PageDefault from '../../../components/PageDefault';

function CadastroCategoria(){

    const [nomeDaCategoria, setNomeDaCategoria] = useState('Filmes');
    const [categorias, setCategoria] = useState([]);
    return(
      <PageDefault>
          <h1>Cadastro de categoria: {nomeDaCategoria} </h1> 

          <form onSubmit={function handleSubmit(infoDoEvento){
            infoDoEvento.preventDefault();
            console.log("voce tentou enviar o form")
            setCategoria([
              ...categorias,
              nomeDaCategoria
            ]);
          }}> 

          <div>
            <label>
                Nome da Categoria
                <input type="text" 
                value ={nomeDaCategoria} //pega o valor do useState
                onChange={function funcaoHandlerQueOErroPediu(infoDoEvento) {//handler tenta caputar o valor do input.. no evento onChange
                console.log('[nomeDaCategoria]', nomeDaCategoria); //
                console.log('[infoDoEvento]', infoDoEvento.target.value);
                setNomeDaCategoria(infoDoEvento.target.value);// target é o alvo da açao q ta sendo feito.. 
                }
              }/>
            </label>

            <label>
                Descrição:
                <textarea type="text" 
                value ={nomeDaCategoria} //pega o valor do useState
                onChange={function funcaoHandlerQueOErroPediu(infoDoEvento) {//handler tenta caputar o valor do input.. no evento onChange
                console.log('[nomeDaCategoria]', nomeDaCategoria); //
                console.log('[infoDoEvento]', infoDoEvento.target.value);
                setNomeDaCategoria(infoDoEvento.target.value);// target é o alvo da açao q ta sendo feito.. 
                }
              }/>
            </label>

            <label>
                Cor da Categoria
                <input 
                type="color" 
                value ={nomeDaCategoria} //pega o valor do useState
                onChange={function funcaoHandlerQueOErroPediu(infoDoEvento) {//handler tenta caputar o valor do input.. no evento onChange
                console.log('[nomeDaCategoria]', nomeDaCategoria); //
                console.log('[infoDoEvento]', infoDoEvento.target.value);
                setNomeDaCategoria(infoDoEvento.target.value);// target é o alvo da açao q ta sendo feito.. 
                }
              }/>
            </label>
          </div>
            <button>
                Cadastrar
            </button>    
          </form> 
          
          <ul>
            {categorias.map((categoria, indice) => {
              return(
                <li key={`${categoria}${indice}`}>
                  {categoria}
                </li>
              )
            }
            )}
          
          </ul>
          <Link to="/">
            ir para HOme
          </Link>
      </PageDefault>
    )
  }

  export default CadastroCategoria;
